// ゲーム開始画面から遷移してきた際に処理を実施する。
window.addEventListener('load', function(){
	// Enterを押下するとゲーム開始のカウントダウンがスタートする処理を呼び出す。
	document.addEventListener(`keydown`, keyDownGameStart);
});
