function gameStart(){


  // 表示する単語リストを定義する。
  // chromeの場合"ff"が正常に動作しない。

  const wordList = gon.wordListAll;

  // 表示するメーターリストを定義する。
  const imgArray =[
    '/assets/メーターPNG/メーター0.png',
    '/assets/メーターPNG/メーター10_yellow.png',
    '/assets/メーターPNG/メーター20_yellow.png',
    '/assets/メーターPNG/メーター30_yellow.png',
