// ゲーム開始画面から遷移してきた際に処理を実施する。
window.addEventListener('load', function(){

	let eng_word = document.getElementById("pronounceAudioInWordlist");
	pronounce(eng_word);

	// 発音ボタンを押下した際に、発音させる処理を定義する。
	function pronounce(eng_word){
		// résuméの場合は音声ファイルの検索ができないため、ファイルを変更
		if (eng_word == "résumé"){
			eng_word = "rezumei";
		};
		let pronunciation = new Audio('/assets/発音/'+ eng_word + '.mp3');
		pronunciation.play();
	};

});
